var wrtc = require('.')()
var Peer = require('simple-peer')

var p1 = new Peer({ initiator: true, wrtc, trickle: false })
var p2 = new Peer({ wrtc, trickle: false })

p1.on('signal', (data) => {
  console.log('p1', data)
  p2.signal(data)
})

p2.on('signal', (data) => {
  console.log('p2', data)
  p1.signal(data)
})

p1.on('connect', () => console.log('connect'))
