'use strict';

var cidCodecs = {
  command: 'codecs',
  describe: 'List available CID codec names.',
  builder: {
    numeric: {
      describe: 'Display the numeric code as well as the codec name',
      type: 'boolean',
      default: false
    }
  },
  handler({
    ctx: {ipfs, print},
    numeric
  }) {
    for (const codec of ipfs.codecs.listCodecs()) {
      if (numeric) {
        print(`${ codec.code }\t${ codec.name }`);
      } else {
        print(codec.name);
      }
    }
  }
};

module.exports = cidCodecs;
