import parseDuration from 'parse-duration';
export default {
  command: 'query <peerId>',
  describe: 'Find the closest Peer IDs to a given Peer ID by querying the DHT.',
  builder: {
    peerId: { type: 'string' },
    timeout: {
      type: 'string',
      coerce: parseDuration
    }
  },
  async handler({
    ctx: {ipfs, print},
    peerId,
    timeout
  }) {
    const seen = new Set();
    for await (const event of ipfs.dht.query(peerId, { timeout })) {
      if (event.name === 'PEER_RESPONSE') {
        event.closer.forEach(peerData => {
          if (seen.has(peerData.id)) {
            return;
          }
          print(peerData.id);
          seen.add(peerData.id);
        });
      }
    }
  }
};