import { commands } from './files/index.js';
export default {
  command: 'files <command>',
  description: 'Operations over mfs files (ls, mkdir, rm, etc)',
  builder(yargs) {
    return yargs.command(commands);
  },
  handler(argv) {
    argv.ctx.print('Type `jsipfs files --help` for more instructions');
  }
};