import parseDuration from 'parse-duration';
export default {
  command: 'flush [path]',
  describe: ' Flush a given path\'s data to disk',
  builder: {
    'cid-base': {
      describe: 'CID base to use',
      default: 'base58btc'
    },
    timeout: {
      type: 'string',
      coerce: parseDuration
    }
  },
  async handler({
    ctx: {ipfs, print},
    path,
    cidBase,
    timeout
  }) {
    const cid = await ipfs.files.flush(path || '/', { timeout });
    const base = await ipfs.bases.getBase(cidBase);
    print(JSON.stringify({ Cid: cid.toString(base.encoder) }));
  }
};