import parseDuration from 'parse-duration';
import { stripControlCharacters } from '../../utils.js';
export default {
  command: 'list',
  describe: 'List all local keys',
  builder: {
    timeout: {
      type: 'string',
      coerce: parseDuration
    }
  },
  async handler({
    ctx: {ipfs, print},
    timeout
  }) {
    const keys = await ipfs.key.list({ timeout });
    keys.forEach(ki => print(`${ ki.id } ${ stripControlCharacters(ki.name) }`));
  }
};