import parseDuration from 'parse-duration';
import { coerceCID } from '../../utils.js';
export default {
  command: 'data <key>',
  describe: 'Outputs the raw bytes in an IPFS object',
  builder: {
    key: {
      type: 'string',
      coerce: coerceCID
    },
    timeout: {
      type: 'string',
      coerce: parseDuration
    }
  },
  async handler({
    ctx: {ipfs, print},
    key,
    timeout
  }) {
    const data = await ipfs.object.data(key, { timeout });
    print(data, false);
  }
};