import parseDuration from 'parse-duration';
export default {
  command: 'ping <peerId>',
  description: 'Measure the latency of a connection',
  builder: {
    count: {
      alias: 'n',
      type: 'integer',
      default: 10
    },
    timeout: {
      type: 'string',
      coerce: parseDuration
    }
  },
  async handler({
    ctx: {ipfs, print},
    count,
    peerId,
    timeout
  }) {
    for await (const pong of ipfs.ping(peerId, {
        count,
        timeout
      })) {
      const {success, time, text} = pong;
      if (success && !text) {
        print(`Pong received: time=${ time } ms`);
      } else {
        print(text);
      }
    }
  }
};