import parseDuration from 'parse-duration';
import { stripControlCharacters } from '../utils.js';
export default {
  command: 'resolve <name>',
  description: 'Resolve the value of names to IPFS',
  builder: {
    recursive: {
      alias: 'r',
      type: 'boolean',
      default: true
    },
    'cid-base': {
      describe: 'Number base to display CIDs in.',
      type: 'string',
      default: 'base58btc'
    },
    timeout: {
      type: 'string',
      coerce: parseDuration
    }
  },
  async handler({
    ctx: {print, ipfs},
    name,
    recursive,
    cidBase,
    timeout
  }) {
    const res = await ipfs.resolve(name, {
      recursive,
      cidBase,
      timeout
    });
    print(stripControlCharacters(res));
  }
};