'use strict';

var stats = require('../resources/stats.js');
var bitswap = require('../resources/bitswap.js');
var repo = require('../resources/repo.js');

var statsRoutes = [
  {
    method: 'POST',
    path: '/api/v0/stats/bitswap',
    ...bitswap.statResource
  },
  {
    method: 'POST',
    path: '/api/v0/stats/repo',
    ...repo.statResource
  },
  {
    method: 'POST',
    path: '/api/v0/stats/bw',
    ...stats.bwResource
  }
];

module.exports = statsRoutes;
